import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';  // Use useNavigate instead of useHistory

const EditRecipe = () => {
    const [recipe, setRecipe] = useState({
        name: '',
        description: '',
        imageUrl: '',
    });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const { id } = useParams();  // Get the recipe ID from the URL
    const navigate = useNavigate();  // Create a navigate function

    useEffect(() => {
        axios.get(`http://localhost:8080/api/recipes/${id}`)
            .then(response => {
                setRecipe(response.data);
                setLoading(false);
            })
            .catch(err => {
                console.error('There was an error fetching the recipe!', err);
                setError(err);
                setLoading(false);
            });
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setRecipe((prevState) => ({
            ...prevState,
            [name]: value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        axios.put(`http://localhost:8080/api/recipes/${id}`, recipe)
            .then(() => {
                navigate('/browse');  // Use navigate to redirect to the browse page
            })
            .catch((err) => {
                console.error('There was an error updating the recipe!', err);
                setError(err);
            });
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div>There was an error loading the recipe.</div>;

    return (
        <div>
            <h1>Edit Recipe</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Name:
                    <input
                        type="text"
                        name="name"
                        value={recipe.name}
                        onChange={handleChange}
                    />
                </label>
                <br />
                <label>
                    Description:
                    <textarea
                        name="description"
                        value={recipe.description}
                        onChange={handleChange}
                    />
                </label>
                <br />
                <label>
                    Image URL:
                    <input
                        type="text"
                        name="imageUrl"
                        value={recipe.imageUrl}
                        onChange={handleChange}
                    />
                </label>
                <br />
                <button type="submit">Save Changes</button>
            </form>
        </div>
    );
};

export default EditRecipe;
